  if(token) {
    getPlaylists();
  }
}, [token]);

  return (
    <>
      <Header /> 
      <Grid playlists = {playlists.map(playlist => (
              <Playlist 
                key={playlist.id}
                name={playlist.name}
                tracks={playlist.uri}
                />
    ))} 
              />

    </>
  );
};

export default Home;
