import {useState} from "react";
import { Redirect } from "react-router";

import { logout } from "../../config";

import { Wrapper, Content, Text, Dropdown } from "./NavBar.styles";

const Navbar = () => {
  const [dropDown, setDropDown] = useState(false);
  const [playlistsPage, setPlaylistsPage] = useState(false);
  const [homePage, setHomePage] = useState(false);

if(homePage === true){
     <Redirect to={'/'}/>
  } else { return null };


if(playlistsPage === true){
  <Redirect to={'/playlists'}/>
} else { return null };


  function showDropdown(e){
    setDropDown(!dropDown)
  }

  return (



  <Wrapper>
    <Content>
    <h1>Mixed Tape</h1>
      <Text>
        <button onClick={()=>setHomePage(true)}>Home</button>
        <button onClick={()=>setPlaylistsPage(true)}>Playlists</button>
        <Dropdown onClick={showDropdown}/>
          {dropDown
          ? (<button onClick={logout}>Log Out</button>)
          : null}
      </Text>
    </Content>
  </Wrapper>
  )
};

export default Navbar;
