import React, { useState } from "react";

//components
import Playlist from "../Playlist";

//styles
import { Wrapper, Content, Icon} from './Form.styles'


const Form = (props) => {
    //modal functionality of form 
    const [formView, setFormView] = useState(false)

    console.log(formView);

    function showForm(e){
        setFormView(true)
    }

    //handling text inputs
    const[input, setInput]=useState({
        trackId: "",
        content: ""
    });

    function handleChange(e){
        const value = e.target.value
        const spotifyId = props.trackId
        setInput({
            content: value,
            trackId: spotifyId
        });
    }

    function handleSubmit(e){
        e.preventDefault();
        props.onSubmit(input);
        setInput({trackId:'', content:''});
        setFormView(false);
    }

return (
    <Wrapper>
        <Icon onClick={showForm}/>
        formView?
        <Content>
        <form className='form-container'onSubmit={handleSubmit}>
            <textarea type='textarea' name='content' rows="7" cols="100"
                placeholder='Insert a description...' value={input.content} onChange={handleChange}/>
            <button type='submit' disabled={!input.content}>Create</button>
        </form>
        </Content>
    </Wrapper>

)
};

export default Form
