const Form = ({tracks, props}) => {
    const[input, setInput]=useState({
        trackId: "",
        content: ""
    });

    function handleChange(e){
        const value = e.target.value
        setInput({
            ...input,
            [e.target.name]:value
        });
    }

    function handleSubmit(e){
        e.preventDefault();
        props.onSubmit(input);
        console.log(input)
        setInput({trackId:'', content:''});
    }

    console.log(tracks)
return (
        <form value={input.trackId } className='form-container'onSubmit={handleSubmit}>
            <input type='text' name='content' 
                placeholder='Insert a description...' value={input.content} onChange={handleChange}/>
            <button type='submit' disabled={!input.content}>Create</button>
        </form>


)
};

export default Form
