import React from "react";

import styled, { keyframes } from "styled-components"

const move = keyframes`
from { height: 50px; }
to { height: 200px; }
`;

export const Wrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  min-height: 50vh;
`;

export const Content = styled.div`
  display: flex;
  align-items: flex-end;
  justify-content: center;
  width: 200px;
  height: 100px;
  position: relative;
  left: 0;
  top: 0;
  overflow: hidden;
  margin: 0 auto;
`;

export const Bar = styled.div`
background-color: #1db954;
width: 50px;
height: 25px;
margin: 0 2px;
animation-name: ${move};
animation-play-state: running;
animation-direction: alternate;
animation-duration: infinite;
animation-delay: ${props => props.wait || '0ms'}
`;
export default function Loader() {

return (
<Wrapper>
    <Content>
        <Bar wait="250ms"/>
        <Bar wait="750ms"/>
        <Bar wait="550ms"/>
        <Bar wait="25ms"/>
        <Bar wait="150ms"/>
    </Content>
</Wrapper>


    )
}
