import { Link } from "react-router-dom";
import { spotify } from "../../App.js";

import { Wrapper, Content } from "./Grid.styles.js";

import Playlist from "../Playlist/index.js";


const Grid = () => {
  const [playlists, setPlaylists] = useState(null);

  const getPlaylists = async() => {
    try {
      await spotify.getUserPlaylists()
      .then((playlists) => {
        setPlaylists(playlists.items);
      });
    } catch (e) {
        console.log('error in playlist loading', e)
    } 
} 
console.log(playlists)

getPlaylists();


return (
  <Wrapper>
    <Content>
    {playlists.map(playlist => (
              <Playlist 
                key={playlist.id}
                name={playlist.name}
                onClick={
                  <Link to={`/${playlist.id}`}/> }
                />
    ))}
    </Content>
  </Wrapper>
)
};

export default Grid;
