import styled from "styled-components";

export const Wrapper = styled.div`
  max-width: 100vw;
`;

export const Content = styled.div`
  background-color: var(--darkGray);
`;

export const Input = styled.div`
  display: flex;
  justify-content: flex-start;
  border-radius: 5px;
  border: solid #000 2px;
  width: 50%vw;

  .album_img {
    width: 40px;
    margin: 10px;

  }

  .left_box {
    display: flex;
    flex-direction: column;
    line-height: .3;
    padding: 0 10px;
  }

  .track_name {
    line-height: 1.2;
  }

  .artist_name {
    display: flex;
    gap: 8px;
    font-size: .8rem;
    line-height: 1.2;
  }

  .album_name {
    padding: 0 150px;

  }

`;
