import styled, {keyframes} from "styled-components";

const fadeIn = keyframes `
0% { opacity: 0; }
100% { opacity: 1; }
`

export const Page_Wrapper = styled.div`
 background-color: black;
 position: absolute;

`;  

export const Login_Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;`;

export const Content = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    padding: 20px 40px;
    border-radius: 99px;
    background-color: #1db954;
    animation: ${fadeIn};
    animation-duration: 
    animation-delay: 8s;


    &:hover,
  &:focus {
    text-decoration: none;
    filter: brightness(1.1);
  }

  a {
    text-decoration: none;
    color: white;
    font-weight: 300;
    font-size: 2rem;
    text-decoration: none;
  }
    `;
