import { Wrapper, Content } from "./Grid.styles.js";

import Playlist from "../Playlist/index.js";

//spotify
import SpotifyWebApi from "spotify-web-api-js";
const spotify = new SpotifyWebApi();

const Grid = ({playlists}) => {

  const getPlaylistsTracks = async() => {
    try {
      await spotify.getPlaylistTracks(playlist.id)
      .then((playlists) => {
        setPlaylists(playlists.items);
      });
    } catch (e) {
        console.log('error in playlist loading', e)
    } 
} 
console.log(playlists)

return (
  <Wrapper>
    <Content>
    {playlists.map(playlist => (
              <Playlist 
                key={playlist.id}
                name={playlist.name}
                onClick={getPlaylistsTracks}
                />
    ))}
    </Content>
  </Wrapper>
)
};

export default Grid;
