import styled from "styled-components";

export const Wrapper = styled.div`
  max-width: 100vw;
  border-radius: 5px;
  margin: 20px;

`;


export const Content = styled.div`
  color: var(--white);
  display: flex;
  justify-content: flex-start;
  padding: 10px;
  width: 50%vw;

  .album_img {
    width: 40px;
    margin: 10px;

  }

  .left_box {
    display: flex;
    flex-direction: column;
    padding: 10px;

  }

  .track_name {
    margin: 0
    flex: 2;
    height: min-content;
    overflow: hidden;

  }

  .artist_name {
    display: flex;
    gap: 8px;
    font-size: .8rem;
    line-height: 1.8;
    color: var(--lightGrey);

  }

  .album_name {
    position: absolute;
    left: 30%;
    flex: 3;


    padding: 0 150px;

  }

`;
