import { createGlobalStyle } from "styled-components";


export const GlobalStyle = createGlobalStyle`
:root {
    --maxWidth: 1280px;
    --white: #fff;
    --lightGrey: #eee;
    --medGrey: #353535;
    --darkGrey: #1c1c1c;
    --fontSuperBig: 2.5rem;
    --fontBig: 1.5rem;
    --fontMed: 1.2rem;
    --fontSmall: 1rem;
  }

  @import url("https://fonts.googleapis.com/css2?family=Shadows+Into+Light&display=swap")@import url("https://fonts.googleapis.com/css2?family=Shadows+Into+Light&display=swap")
  * {
    box-sizing: border-box;
    font-family: 'Abel', sans-serif, 'Shadows Into Light';
  }

  body {
    margin: 0;
    padding: 0;
    box-sizing: border-box;

    h1 {
      font-size: 2rem;
      font-weight: 600;
      color: var(--white);
    }

    h3 {
      font-size: 1.1rem;
      font-weight: 600;
    }

    p {
      font-size: 1rem;
      color: var(--white);
    }
  }
`;
