import styled from "styled-components";

export const Wrapper = styled.div`
  max-width: 100vw;
  background-color: var(--darkGrey);
  border-radius: 5px;
  margin: 20px;
  :hover {
    opacity: 0.8;


`;

export const Content = styled.div`
`;

export const Input = styled.div`
  color: var(--white);
  display: flex;
  justify-content: flex-start;
  padding: 10px;
  width: 50%vw;

  .album_img {
    width: 40px;
    margin: 10px;

  }

  .left_box {
    display: flex;
    flex-direction: column;
    padding: 0 10px;
  }

  .track_name {
    line-height: 1;
  }

  .artist_name {
    display: flex;
    gap: 8px;
    font-size: .8rem;
    line-height: 1;
  }



  .album_name {
    position: absolute;
    left: auto;
    padding: 0 150px;

  }

`;
