const localStor = {
    accessToken: 'spotify_access_token',
    refreshToken: 'spotify_refresh_token',
    expireTime: 'spotify_token_expire_time',
    timestamp: 'spotify_token_timestamp',
  }

  const LOCALSTORAGE_VALUES = {
    accessToken: window.localStorage.getItem(localStor.accessToken),
    refreshToken: window.localStorage.getItem(localStor.refreshToken),
    expireTime: window.localStorage.getItem(localStor.expireTime),
    timestamp: window.localStorage.getItem(localStor.timestamp),
  };

const getToken = () => {
const queryString = window.location.search;
const urlParams = new URLSearchParams(queryString);
const tokenParams = {
    [localStor.accessToken]: urlParams.get('access_token'),
    [localStor.refreshToken]: urlParams.get('refresh_token'),
    [localStor.expireTime]: urlParams.get('expires_in'),
  };  

console.log(tokenParams[localStor.accessToken]);
return tokenParams[localStor.accessToken];
}



export const accessToken = getToken();
